import { FunctionComponent, useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import * as yup from "yup";
import User from "../interfaces/User";
import { getUserById, updateUser } from "../services/usersServices";
import { useFormik } from "formik";

interface UserProfileProps {
  setUserInfo: Function;
  userInfo: any;
}

const UserProfile: FunctionComponent<UserProfileProps> = ({ userInfo, setUserInfo }) => {
  const navigate = useNavigate();

  let { _id } = useParams();
  let [user, setUser] = useState<User>({
    firstName: "",
    lastName: "",
    email: "",
    password: "",
  });

  useEffect(() => {
    console.log(_id);

    getUserById(String(_id))
      .then((res) => {
        setUser(res.data);
      })
      .catch((error) => console.log(error));
  }, []);
  let formik = useFormik({
    initialValues: {
      firstName: user.firstName,
      lastName: user.lastName,
      email: user.email,
      password: user.password,
    },
    enableReinitialize: true,

    validationSchema: yup.object({
      firstName: yup.string().required().min(2),
      lastName: yup.string().required().min(2),
      email: yup.string().required().email(),
      password: yup.string().required().min(8),
    }),
    onSubmit: (values) => {
      console.log(_id);
      updateUser(String(_id), values)
        .then((res) => {
          navigate("/");
          alert("user updated successfully!");
        })
        .catch((error) => {
          if (error.response && error.response.status === 400) {
            alert(error.response.data); // Display the error message in an alert
          } else {
            console.error("Error:", error);
          }
        });
    },
  });
  return <></>;
};

export default UserProfile;
