import { FunctionComponent, useState } from "react";
import { searchProducts } from "../services/productsServices";
import Product from "../interfaces/Product";

interface SearchProps {
  searchTerm: string;
  handleSearch: (value: string) => void;
}

const Search: FunctionComponent<SearchProps> = ({ searchTerm, handleSearch }) => {
  const [searchResults, setSearchResults] = useState<any[]>([]);

  const localHandleSearch = (value: string) => {
    handleSearch(value);
    searchProducts(value)
      .then((res) => {
        let results = res.filter((product: Product) => {
          return value && product.name.toLowerCase().includes(value);
        });
        setSearchResults(results);
      })
      .catch((error) => console.log(error));
  };

  return <>This is Search</>;
};

export default Search;
