import { FunctionComponent } from "react";
import { Link, useNavigate } from "react-router-dom";
import { TokenDetails } from "../App";

interface NavbarProps {
  setUserInfo: Function;
  userInfo: TokenDetails | false;
}
const Navbar: FunctionComponent<NavbarProps> = ({ userInfo, setUserInfo }) => {
  let navigate = useNavigate();
  let render = () => setUserInfo(!userInfo);

  let logout = () => {
    sessionStorage.removeItem("userInfo");
    sessionStorage.removeItem("token");
    navigate("/");
    alert("You have logged out!");
    render();
  };

  return (
    <>
      <nav className="navbar navbar-expand-lg bg-body-tertiary bg-dark px-4" data-bs-theme="dark">
        <div className="container-fluid">
          <a className="navbar-brand" href="/">
            <img src="logo-no-background.png" alt="Bootstrap" width="150" />
          </a>
          <button
            className="navbar-toggler"
            type="button"
            data-bs-toggle="collapse"
            data-bs-target="#navbarNavDropdown"
            aria-controls="navbarNavDropdown"
            aria-expanded="false"
            aria-label="Toggle navigation"
          >
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse justify-content-between" id="navbarNavDropdown">
            {/* //////Profile button */}
            <div className="btn-group dropend" role="group">
              <Link
                type="button"
                className="btn btn-primary dropdown-toggle"
                data-bs-toggle="dropdown"
                aria-expanded="false"
                to={""}
              >
                <i className="bi bi-person h2" />
              </Link>
              <ul className="dropdown-menu">
                {!userInfo && (
                  <>
                    <li>
                      <Link className="dropdown-item" to="/login">
                        Login
                      </Link>
                    </li>
                    <li>
                      <Link className="dropdown-item" to="/register">
                        Register
                      </Link>
                    </li>
                  </>
                )}
                {userInfo && (
                  <>
                    <li>
                      <Link className="dropdown-item" to={`users/user-profile/${userInfo._id}`}>
                        User Profile
                      </Link>
                    </li>
                    <li>
                      <Link className="dropdown-item" to="/" onClick={logout}>
                        Logout
                      </Link>
                    </li>
                  </>
                )}
              </ul>
              <Link to={"users/user-cart"}>
                <i className="bi bi-basket3" style={{ fontSize: "22px", color: "white" }}></i>
              </Link>
            </div>
            <ul className="navbar-nav">
              <li className="nav-item">
                <Link className="nav-link active" aria-current="page" to="/">
                  Home
                </Link>
              </li>
              {userInfo && "isAdmin" in userInfo && (
                <>
                  <li className="nav-item">
                    <Link className="nav-link" to="crm">
                      CRM-Admin Panel
                    </Link>
                  </li>
                </>
              )}
            </ul>
          </div>
        </div>
      </nav>
    </>
  );
};

export default Navbar;
