import { FunctionComponent, useEffect, useState } from "react";
import Product from "../interfaces/Product";
import { getProducts } from "../services/productsServices";
import { error } from "console";
import { Link, useNavigate } from "react-router-dom";
import { updateCart } from "../services/cartsServices";

interface HomeProps {
  setUserInfo: Function;
  userInfo: any;
}

const Home: FunctionComponent<HomeProps> = ({ userInfo, setUserInfo }) => {
  let navigate = useNavigate();

  let [product, setProduct] = useState<Product[]>([]);

  useEffect(() => {
    getProducts()
      .then((res) => setProduct(res.data))
      .catch((error) => console.log(error));
  }, []);

  let handleAddToCart = (product: Product) => {
    if (userInfo) {
      updateCart(product)
        .then((res) => alert("Product added to cart!"))
        .catch((err) => console.log(err));
    } else {
      alert("you need to sign in");
    }
  };
  return (
    <>
      <div className="container pt-5">
        {/* <label>
          Pick a fruit:
          <select name="selectedFruit">
            <option value="apple">Apple</option>
            <option value="banana">Banana</option>
            <option value="orange">Orange</option>
          </select>
        </label> */}
        <div className="row justify-content-center">
          {product.length ? (
            product.map((product: Product) => (
              <div
                key={product._id}
                className="card bg-light mx-2"
                style={{ width: "18rem", marginBottom: "10px" }}
              >
                <i className="bi bi-heart text-danger text-end"></i>
                <Link
                  to={`products/product-info/${product._id}`}
                  style={{ textDecoration: "none", color: "black" }}
                >
                  <img src={product.image} className="card-img-top" alt={product.imageAlt} />
                  <div className="card-body">
                    <p className="card-text">{product.license}</p>
                    <p className="card-text">{product.name}</p>
                    <h5 className="card-title">{product.price}$</h5>
                  </div>
                </Link>
                <div className="row  flex-grow-1">
                  <button
                    type="button"
                    className="btn mb-2 "
                    onClick={() => handleAddToCart(product)}
                    style={{ backgroundColor: "white" }}
                  >
                    ADD TO CART
                  </button>
                  <button
                    type="button"
                    className="btn mb-2"
                    onClick={() => handleAddToCart(product)}
                  >
                    REMOVE FROM CART
                  </button>
                </div>
              </div>
            ))
          ) : (
            <h4>no products here</h4>
          )}
        </div>
      </div>
      ;
    </>
  );
};

export default Home;
