import { FunctionComponent, useEffect, useState } from "react";
import User from "../interfaces/User";
import { deleteUser, getUsers } from "../services/usersServices";
import { Link } from "react-router-dom";

interface CRMProps {
  userInfo: any;
}

const CRM: FunctionComponent<CRMProps> = ({ userInfo }) => {
  let [users, setUsers] = useState<User[]>([]);
  let [usersChanged, setUsersChanged] = useState<boolean>(false);
  const isAdmin = userInfo.isAdmin;
  let render = () => {
    setUsersChanged(!usersChanged);
  };
  useEffect(() => {
    if (!isAdmin) {
      ("You are not authorize");
    } else {
      getUsers()
        .then((res) => setUsers(res.data))
        .catch((err) => console.log(err));
    }
  }, [usersChanged]);

  let handleDelete = (user: User) => {
    if (window.confirm("Are you sure you want to delete this user?")) {
      deleteUser(user._id as string)
        .then((res) => {
          alert("User removed successfully");

          render();
        })
        .catch((err) => console.log(err));
    }
  };
  return (
    <>
      <div className="container">
        {users.length ? (
          <table className="table">
            <thead>
              <tr className="table-success">
                <td>User Id</td>
                <td>Name</td>
                <td>Mail</td>
                <td>role</td>
                <td>Edit User</td>
                <td>Delete User</td>
              </tr>
            </thead>
            <tbody>
              {users.map((user: User) => (
                <tr key={user._id}>
                  <td>{user._id}</td>
                  <td>
                    {user.firstName}&nbsp;
                    {user.lastName}
                  </td>
                  <td>{user.email}</td>
                  <td>{user.isAdmin ? "Admin" : "User"}</td>
                  <td>
                    <Link to={`/users/user-profile/${user._id}`}>
                      <i className="bi bi-pencil-square" />
                    </Link>
                  </td>
                  <td>
                    <Link to="" onClick={() => handleDelete(user)}>
                      <i className="bi bi-trash3" style={{ color: "#d23737" }}></i>
                    </Link>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <h3>There are no users</h3>
        )}
      </div>
    </>
  );
};

export default CRM;
