import { FunctionComponent, useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import * as yup from "yup";
import User from "../interfaces/User";

interface UserProfileProps {
  setUserInfo: Function;
  userInfo: any;
}

const UserProfile: FunctionComponent<UserProfileProps> = ({ userInfo, setUserInfo }) => {
  let { _id } = useParams();
  let [user, setUser] = useState<User>({
    firstName: "",
    lastName: "",
    email: "",
    password: "",
  });

  useEffect(() => {
    getuserById(String(_id))
      .then((res) => {
        setuser(res.data);
      })
      .catch((error) => console.log(error));
  }, []);
  let formik = useFormik({
    initialValues: {
      firstName: "",
      lastName: "",
      email: "",
      password: "",
    },
    enableReinitialize: true,

    validationSchema: yup.object({
      name: yup.string().required().min(2),
      license: yup.string().required().min(2),
      description: yup.string().required().min(6),
      image: yup.string(),
      imageAlt: yup.string(),
      price: yup.number().required(),
      category: yup.string().required(),
      inStock: yup.string().required(),
    }),
    onSubmit: (values) => {
      console.log(_id);
      updateuser(values, String(_id))
        .then((res) => {
          navigate("/");
          alert("user updated successfully!");
        })
        .catch((error) => {
          if (error.response && error.response.status === 400) {
            alert(error.response.data); // Display the error message in an alert
          } else {
            console.error("Error:", error);
          }
        });
    },
  });
  return <></>;
};

export default UserProfile;
