import { FunctionComponent, useEffect, useState } from "react";
import Cart from "../interfaces/Cart";
import { getCart } from "../services/cartsServices";

interface userCartProps {
  setUserInfo: Function;
  userInfo: any;
}

const userCart: FunctionComponent<userCartProps> = ({ userInfo, setUserInfo }) => {
  let [cart, setCart] = useState<Cart[]>([]);
  let [CartChanged, setCartChanged] = useState<boolean>(false);

  useEffect(() => {
    getCart(userInfo._id)
      .then((res) => {
        setCart(res.data);
      })
      .catch((err) => console.log(err));
  }, [setCartChanged]);
  return (
    <>
      {cart.length ? (
        <table className="table">
          <thead>
            <tr className="table-success">
              <td>User Id</td>
              <td>Name</td>
              <td>Mail</td>
              <td>Password</td>
              <td>role</td>

              <td>Edit User</td>
              <td>Delete User</td>
            </tr>
          </thead>
          <tbody>
            {cart.map((user: User) => (
              <tr key={user._id}>
                <td>{user._id}</td>
                <td>{user.firstName}</td>
                <td>{user.email}</td>
                <td>{user.password}</td>
                <td>{user.role}</td>
                <td>
                  <Link to={`/cart/user-profile/${user._id}`}>
                    <i className="fa-regular fa-pen-to-square" />
                  </Link>
                </td>
                <td>
                  <Link to="" onClick={() => handleDelete(user)}>
                    <i className="fa-solid fa-trash" style={{ color: "#d23737" }}></i>
                  </Link>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <h3>There are no cart</h3>
      )}
    </>
  );
};

export default userCart;
