import { FunctionComponent, useEffect, useState } from "react";
import Cart from "../interfaces/Cart";
import { getCart } from "../services/cartsServices";
import Product from "../interfaces/Product";

interface productCartProps {
  setproductInfo: Function;
  productInfo: any;
}

const productCart: FunctionComponent<productCartProps> = ({ productInfo, setproductInfo }) => {
  let [products, setProducts] = useState<Product[]>([]);
  let [CartChanged, setCartChanged] = useState<boolean>(false);

  useEffect(() => {
    getCart(productInfo._id)
      .then((res) => {
        setProducts(res.data);
      })
      .catch((err) => console.log(err));
  }, [setCartChanged]);
  return (
    <>
      {products.length ? (
        <table className="table">
          <thead>
            <tr className="table-success">
              <td>product Id</td>
              <td>Name</td>
              <td>Mail</td>
              <td>Password</td>
              <td>role</td>

              <td>Edit product</td>
              <td>Delete product</td>
            </tr>
          </thead>
          <tbody>
            {products.map((product: Product) => (
              <tr key={product._id}>
                <td>{product._id}</td>
                <td>{product.name}</td>
                <td>{product.license}</td>
                <td>{product.price}</td>
                <td>{product.role}</td>
                <td>
                  <Link to={`/cart/product-profile/${product._id}`}>
                    <i className="fa-regular fa-pen-to-square" />
                  </Link>
                </td>
                <td>
                  <Link to="" onClick={() => handleDelete(product)}>
                    <i className="fa-solid fa-trash" style={{ color: "#d23737" }}></i>
                  </Link>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <h3>There are no cart</h3>
      )}
    </>
  );
};

export default productCart;
